static void
gst_my_filter_loopfunc (GstElement *element)
{
  GstMyFilter *filter = GST_MY_FILTER (element);
  GstData *data;

  /* acquire data */
  data = gst_pad_pull (filter->sinkpad);

  /* send data */
  gst_pad_push (filter->srcpad, data);
}

